import React, { Component } from 'react';
import propTypes from 'prop-types';
import axios from 'axios';
import '../styles/App.css';
import News from './contentKiriProps.js';



class Search extends Component {
    constructor(props){
        super(props);
        this.state={
          listNews : [],
          username : "",
          isLogin : false,
          search : ""
        }
      }

    // componentDidMount = () => {
    // const self = this;
    // axios
    // .get(myReqUrl)
    // .then(function(response){

    //     self.setState({listNews: response.data.articles})
    //     console.log(response.data)
    // })
    // .catch(function(error){
    //     console.log(error)
    // })
    // }



    render() {
    const {listNews, username, isLogin} = this.state;
    return (
        <div>
        <section className="topsection">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-md-4 col-sm-12 offset-md-0">
                        <div id="classSearch">
                            <form className="navbar-form navbar-center" action="http:127.0.0.1:5000/pembeli/produk/search">
                                <div className="input-group col-lg-10" style={{width:"100%", paddingTop: "2px"}}>

                                    <input 
                                    type="text" 
                                    name='search' 
                                    className="form-control" 
                                    id="search" 
                                    value={this.props.keyword} 
                                    placeholder={this.props.placeholder} 
                                    onChange={this.props.doSearch}
                                    />
                                    {/* <input type="text" name='keyword' className="form-control" placeholder="Search"></input> */}
                                    <div className="input-group-btn">
                                        <button className="btn btn-success" type="submit">
                                            <i className="glyphicon glyphicon-search"></i>
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
        </div>
        </section>

        <div className="album background" id="level-0">
            <div className="headerCart">
            <div className="col-md-6 col-sm-12 offset-md-0">
                <div className="flex-container">
                {listNews.map((item, key) => {
                    return <News key={key} title={item.title} img={item.urlToImage} content={item.content} />
                })}
                </div>
            </div>
            </div>
        </div>
        </div>

    );
  }
}

Search.propTypes = {
    title: propTypes.string.isRequired,
    placeholder: propTypes.string.isRequired
};


export default Search;
