import React, { Component } from 'react';

import './styles/App.css';
import './styles/bootstrap.min.css';

import axios from 'axios';

import Header from './components/header.js';
import Search from './components/search.js';
import ContentKiri from './components/contentKiri.js';
import ContentKanan from './components/contentKanan.js';
import Footer from './components/footer.js';


const baseUrl = "https://newsapi.org/v2";
const searchCategory = "/top-headlines";
const searchCategory2 = "/everything?q=";
const searchSource  = "bbc-news";
const keyAPI = "31a74b58e4bb4e1bb1d7bb0542dd1d81";
const search = 'sport';

const myReqUrl = baseUrl + searchCategory + "?sources="+ searchSource + "&apiKey=" + keyAPI
const myReqUrl2 = baseUrl + searchCategory2 + search + "&apiKey=" + keyAPI


class App extends Component {
  handleInputChange = e => {
    console.log('event', e.target.value);
    let value = e.target.value;

    this.setState(
        {
        search: value
        },
        () => {
        this.searchNews(value);
        }
    );
    };

    searchNews = async keyword => {
    console.log('searchNews', keyword);
    const self = this;
    if(keyword.length > 2){
        try {
        const response = await axios.get(baseUrl + "/everything?q=" + keyword + "&apiKey=" + keyAPI);
        console.log(response);
        self.setState({listNews : response.data.articles})
        } catch (error){
            console.log(error);
        }
        }
    };

  render() {
    return (
      <div className="App">
        <header className="App-header">

        <div>
            {/* <Header/> */}
            <Search doSearch={this.handleInputChange}/>
            {/* <ContentKiri/> */}
            <ContentKanan/>
            <Footer/>
        </div>

        </header>
      </div>
    );
  }
}

export default App;
