import React, { Component } from 'react';
import propTypes from 'prop-types';

import '../styles/App.css';

const baseUrl = "https://newsapi.org/v2";
const searchCategory = "/top-headlines";
const searchCategory2 = "/everything?q=";
const searchSource  = "bbc-news";
const keyAPI = "31a74b58e4bb4e1bb1d7bb0542dd1d81";
const search = 'sport';

const myReqUrl = baseUrl + searchCategory + "?sources="+ searchSource + "&apiKey=" + keyAPI
const myReqUrl2 = baseUrl + searchCategory2 + search + "&apiKey=" + keyAPI


class Search extends Component {

    render() {
    return (
        <section className="topsection">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-md-4 col-sm-12 offset-md-0">
                        <div id="classSearch">
                            <form className="navbar-form navbar-center" action="http:127.0.0.1:5000/pembeli/produk/search">
                                <div className="input-group col-lg-10" style={{width:"100%", paddingTop: "2px"}}>

                                    <input 
                                    type="text" 
                                    name='search' 
                                    className="form-control" 
                                    id="search" 
                                    value={this.props.keyword} 
                                    placeholder={this.props.placeholder} 
                                    onChange={this.props.doSearch}
                                    />
                                    {/* <input type="text" name='keyword' className="form-control" placeholder="Search"></input> */}
                                    <div className="input-group-btn">
                                        <button className="btn btn-success" type="submit">
                                            <i className="glyphicon glyphicon-search"></i>
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
        </div>
        </section>


    );
  }
}

Search.propTypes = {
    title: propTypes.string.isRequired,
    placeholder: propTypes.string.isRequired
};


export default Search;
